(function() {

  window.FileBrowser = {
    Models: {},
    Collections: {},
    Views: {},
    wrapper: '#file-browser',
    name: '',
    files: [],
    maxColumns: 0,
    init: function(options) {
      _.extend(this, options);
      this.update(options.files, true);
      return this;
    },
    update: function(files, force) {
      if (files && $(this.wrapper).length > 0) {
        if ((this.files !== files) || force) {
          this.fileTree = this._createFileTree(files);
          this.files = files;
        }
        if (this.Views.fileWindow) {
          this.Views.fileWindow.off;
          this.Views.fileWindow.$el.html('');
        }
        this.Views.fileWindow = new this.Views.FileWindow({
          model: this.fileTree
        });
        this.Views.fileWindow.render();
        $(this.wrapper).html(this.Views.fileWindow.el);
      }
      return this;
    },
    _createFileTree: function(files) {
      var tree;
      tree = new FileBrowser.Models.Directory({
        name: 'root',
        root: true
      });
      _.each(files, function(serverFile) {
        var currentDir, fileName, hierarchy, splitName;
        if (serverFile.constructor === String) {
          serverFile = {
            name: serverFile
          };
        }
        if (!serverFile.status) {
          serverFile.status = 'noStatus';
        }
        if (!serverFile.content) {
          serverFile.content = '';
        }
        splitName = serverFile.name.split('/');
        currentDir = tree;
        hierarchy = [];
        fileName = splitName.pop();
        _.each(splitName, function(name, index) {
          var parent;
          if (!currentDir.get('directories').where({
            name: name
          })[0]) {
            currentDir.get('directories').add({
              name: name,
              hierarchy: hierarchy
            });
          }
          hierarchy.push(currentDir);
          parent = currentDir;
          currentDir = currentDir.get('directories').where({
            name: name
          })[0];
          return currentDir.set({
            parentDirectory: parent
          });
        });
        if (fileName !== '') {
          return currentDir.get('files').add({
            name: fileName,
            status: serverFile.status,
            content: serverFile.content,
            hierarchy: hierarchy,
            parentDirectory: currentDir
          });
        }
      });
      return tree;
    }
  };

}).call(this);
