(function() {

  $(function() {
    var socialPopup, startUnlockTimer, textTicker, ticker, timer, unlock;
    $('.codeschool-banner.show').show().animate({
      'bottom': '0'
    }, 400);
    $('html').delegate('code.input, code.input-inline', 'mousedown', function(evt) {
      var clickedText, editor;
      editor = CourseApp.editor_views[0].editor;
      clickedText = $(evt.target).html();
      if (!editor.getValue().match(clickedText)) {
        return textTicker({
          speed: 70,
          editor: editor,
          text: clickedText
        });
      }
    });
    ticker = false;
    textTicker = function(options) {
      var count, fullText, tickerSpeed;
      if (options == null) {
        options = {};
      }
      count = 0;
      fullText = "";
      tickerSpeed = options.speed;
      if (options.text.length > 15) {
        tickerSpeed = options.speed / 2;
      }
      if (!ticker) {
        return ticker = setInterval(function() {
          var letter;
          letter = options.text[count];
          count += 1;
          if (letter) {
            options.editor.appendToInput(letter);
          } else {
            clearInterval(ticker);
            ticker = false;
          }
          if (count === 1) {
            return options.editor.focus();
          }
        }, tickerSpeed);
      }
    };
    $('.progress-bar').delegate('a', 'mouseover', function(evt) {
      var plXPos, plYPos, progressLink, tooltip;
      progressLink = $(evt.currentTarget);
      plXPos = progressLink.position().left;
      plYPos = progressLink.position().top;
      tooltip = $(evt.currentTarget).next('span');
      tooltip.css({
        'position': 'absolute'
      });
      tooltip.css({
        'left': (plXPos + (progressLink.width() / 2)) - (tooltip.outerWidth() / 2),
        'top': plYPos + progressLink.height() + 8
      });
      return tooltip.show();
    });
    $('.progress-bar').delegate('a', 'mouseout', function(evt) {
      return $(evt.currentTarget).next('span').hide();
    });
    timer = unlock = true;
    startUnlockTimer = function() {
      clearTimeout(timer);
      return timer = setTimeout(function() {
        return unlock = true;
      }, 1000);
    };
    $('#challenge-panels').ajaxSuccess(function(evt) {
      var currentNode;
      startUnlockTimer();
      if (unlock) {
        unlock = false;
        currentNode = $('.progress-bar').find("a[href='" + evt.currentTarget.baseURI + "']").parent();
        if (currentNode.index() === $('.progress-bar li').last().index() && currentNode.prev('li').hasClass('completed')) {
          return currentNode.addClass('completed');
        } else {
          return currentNode.prev('li').addClass('completed');
        }
      }
    });
    $('.share-twitter').click(function(evt) {
      evt.preventDefault();
      return socialPopup({
        width: 400,
        height: 300,
        title: "Tweet about Try Git",
        url: $(evt.currentTarget).attr('href')
      });
    });
    $('.share-facebook').click(function(evt) {
      evt.preventDefault();
      return socialPopup({
        width: 1024,
        height: 500,
        title: "Share on Facebook",
        url: $(evt.currentTarget).attr('href')
      });
    });
    return socialPopup = function(options) {
      var modalHeight, modalWidth, socialMediaWindow;
      if (options == null) {
        options = {};
      }
      modalWidth = options.width;
      modalHeight = options.height;
      socialMediaWindow = window.open(options.url, options.title, "menubar=0,resizable=1,width=" + modalWidth + ",height=" + modalHeight);
      return setTimeout(function() {
        var xPos, yPos;
        xPos = ($(window).width() / 2) - (modalWidth / 2);
        yPos = ($(window).height() / 2) - (modalHeight / 2);
        console.log("" + xPos + " x " + yPos);
        return socialMediaWindow.moveTo(xPos, yPos);
      }, 10);
    };
  });

}).call(this);
